    import TransportIcon from '@/components/Icons/TransportIcon'
    import CharityIcon from '@/components/Icons/CharityIcon'
    import HostingIcon from '@/components/Icons/HostingIcon'
    import 'hooper/dist/hooper.css'
    import { appTab } from '@/utils/frontend'
    import paymentApi from '@/api/payment'
    import I18nVue from '@/I18nVue'
    import { Hooper, Slide, Pagination as HooperPagination } from 'hooper'
    import AppBreadCrumb from '@/components/AppBreadCrumb'
    import { ChevronRightIcon, ChevronLeftIcon } from 'vue-feather-icons'

    export default {
        name: 'index',
        components: {
            AppBreadCrumb,
            ChevronRightIcon,
            ChevronLeftIcon,
            Hooper,
            Slide,
            HooperPagination,
            I18nVue,
            MobileIcon,
            BuildIcon,
            GlobeIcon,
            EducationIcon,
            TVIcon,
            TelephonyIcon,
            TransportIcon,
            CharityIcon,
            HostingIcon,
        },
        data() {
            return {
                loading: true,
                category_list: [],
            }
        },
        mounted() {
            const $tabHeaderItems = document.querySelectorAll('[data-item="header"]')
            const $tabContentItems = document.querySelectorAll('[data-item="content"]')
            appTab($tabHeaderItems, $tabContentItems)
            this.loading = true
            paymentApi
                .get_category_list()
                .then((r) => (this.category_list = r.data))
                // .catch((err) => console.log('Error on Authentication: ', err.data))
                .finally(() => (this.loading = false))
            if (!this.$route.params.id) {
                this.$router.push({ name: 'PaymentRoute', params: { id: 1 } })
            }
        },
    }
</script>

